$(document).ready(function() {
    //открытие и закрытие сундука по нажатию
    let chest = $('.cart__chest');

    chest.on('click', function(e){
        if (chest.hasClass('active')){
        chest.removeClass('active');
        } else {
            chest.addClass('active');
        }
    });

    // открытие и закрытие основного контента страницы
    let openbtn = $('.open'),
        cart = $('.cart'),
        close = $('.cart-header__close');

    openbtn.on('click', function (e) {
        e.preventDefault();
        cart.addClass('show');
        openbtn.addClass('hide');
    });

    close.on('click', function (e) {
        e.preventDefault();
        cart.removeClass('show');
        openbtn.removeClass('hide');
    });

    // вычисление прогресса на основе введенных чисел в html
    let range = $('.progress span'),
        point = Number($('.value').html()),
        max = Number($('.max').html());

    range.width(function(e){
        let procent = point / max;
        return procent * 100 + '%';
    });
})